mapper.select:
select 'true' as QUERYID,
,
from SYS_EXCEPTION where EXCEPTION_ID = #{exceptionId,jdbcType=VARCHAR}


select 

,

from SYS_EXCEPTION


     AND EXCEPTION_ID = #{exceptionId,jdbcType=VARCHAR}


     AND EXCEPTION_MSG = #{exceptionMsg,jdbcType=VARCHAR}


     AND EXCEPTION_URI = #{exceptionUri,jdbcType=VARCHAR}


     AND TRANSACTION_ID = #{transactionId,jdbcType=VARCHAR}


     AND CREATE_TIME = #{createTime,jdbcType=TIMESTAMP}


     AND UPDATE_TIME = #{updateTime,jdbcType=TIMESTAMP}


     AND CREATE_USER = #{createUser,jdbcType=VARCHAR}


     AND UPDATE_USER = #{updateUser,jdbcType=VARCHAR}


     AND EXCEPTION_INFO = #{exceptionInfo,jdbcType=CLOB}

