mapper.insert:
INSERT INTO SYS_QUARTZ_JOB (ID, BEAN_NAME, CRON_EXPRESSION, 
  IS_PAUSE, JOB_NAME, METHOD_NAME, 
  PARAMS, REMARK, CREATE_TIME, 
  UPDATE_TIME, CREATE_USER, UPDATE_USER
  )
VALUES (#{id,jdbcType=VARCHAR}, #{beanName,jdbcType=VARCHAR}, #{cronExpression,jdbcType=VARCHAR}, 
  #{isPause,jdbcType=DECIMAL}, #{jobName,jdbcType=VARCHAR}, #{methodName,jdbcType=VARCHAR}, 
  #{params,jdbcType=VARCHAR}, #{remark,jdbcType=VARCHAR}, #{createTime,jdbcType=TIMESTAMP}, 
  #{updateTime,jdbcType=TIMESTAMP}, #{createUser,jdbcType=VARCHAR}, #{updateUser,jdbcType=VARCHAR}
  )

mapper.select:
SELECT 

FROM SYS_QUARTZ_JOB


     AND ID = #{id,jdbcType=VARCHAR}


     AND BEAN_NAME = #{beanName,jdbcType=VARCHAR}


     AND CRON_EXPRESSION = #{cronExpression,jdbcType=VARCHAR}


     AND IS_PAUSE = #{isPause,jdbcType=DECIMAL}


     AND JOB_NAME = #{jobName,jdbcType=VARCHAR}


     AND METHOD_NAME = #{methodName,jdbcType=VARCHAR}


     AND PARAMS = #{params,jdbcType=VARCHAR}


     AND REMARK = #{remark,jdbcType=VARCHAR}


     AND CREATE_TIME = #{createTime,jdbcType=TIMESTAMP}


     AND UPDATE_TIME = #{updateTime,jdbcType=TIMESTAMP}


     AND CREATE_USER = #{createUser,jdbcType=VARCHAR}


     AND UPDATE_USER = #{updateUser,jdbcType=VARCHAR}
